mapper.select:
WITH RankedStudents AS ( SELECT s.DEPT_CODE, r.YEAR, r.SMT, d.DEPT_NAME, s.STU_NO, s.STU_GRADE, s.STU_NAME, s.SCH_STATUS, ROUND(AVG(CASE WHEN r.GRADE = 'A+' THEN 4.5 WHEN r.GRADE = 'A' THEN 4.0 WHEN r.GRADE = 'B+' THEN 3.5 WHEN r.GRADE = 'B' THEN 3.0 WHEN r.GRADE = 'C+' THEN 2.5 WHEN r.GRADE = 'C' THEN 2.0 WHEN r.GRADE = 'D+' THEN 1.5 WHEN r.GRADE = 'D' THEN 1.0 WHEN r.GRADE = 'F' THEN 0.0 END), 1) AS RESULTSAVG, ROW_NUMBER() OVER ( PARTITION BY s.DEPT_CODE, s.STU_GRADE ORDER BY ROUND(AVG(CASE WHEN r.GRADE = 'A+' THEN 4.5 WHEN r.GRADE = 'A' THEN 4.0 WHEN r.GRADE = 'B+' THEN 3.5 WHEN r.GRADE = 'B' THEN 3.0 WHEN r.GRADE = 'C+' THEN 2.5 WHEN r.GRADE = 'C' THEN 2.0 WHEN r.GRADE = 'D+' THEN 1.5 WHEN r.GRADE = 'D' THEN 1.0 WHEN r.GRADE = 'F' THEN 0.0 END), 1) DESC ) AS r FROM university.RESULTS r JOIN university.STUDENT s ON r.STU_NO = s.STU_NO JOIN university.DEPARTMENT d ON s.DEPT_CODE = d.DEPT_CODE WHERE r.YEAR = 2024 AND r.SMT = 1 GROUP BY s.DEPT_CODE, d.DEPT_NAME, s.STU_NO, s.STU_NAME, s.STU_GRADE, r.YEAR, r.SMT ) SELECT DEPT_CODE, YEAR, SMT, DEPT_NAME, STU_NO, STU_GRADE, STU_NAME, SCH_STATUS, RESULTSAVG, CASE WHEN r = 1 THEN '최우수 장학금' WHEN r = 2 THEN '우수 장학금' WHEN r = 3 THEN '장려 장학금' ELSE NULL END AS SCH_NAME, CASE WHEN r = 1 THEN 1 WHEN r = 2 THEN 2 WHEN r = 3 THEN 3 ELSE NULL END AS SCH_NO FROM RankedStudents WHERE r < 4
AND r = #{scholarship_type}
AND DEPT_CODE = #{DEPT_CODE}
AND STU_GRADE = #{STU_GRADE}
ORDER BY DEPT_CODE, STU_GRADE, r
